import{_ as t,a as e}from"./chunks/st0037-02.2ff589f3.js";import{_ as a,c as r,o as s,a as o}from"./app.7586e3b1.js";const p="/st0037-03.png",P=JSON.parse('{"title":"凹语言支持P5儿童编程","description":"","frontmatter":{},"headers":[],"relativePath":"smalltalk/st0037.md"}'),l={name:"smalltalk/st0037.md"},c=o('<h1 id="凹语言支持p5儿童编程" tabindex="-1">凹语言支持P5儿童编程 <a class="header-anchor" href="#凹语言支持p5儿童编程" aria-hidden="true">#</a></h1><ul><li>时间：2024-02-14</li><li>撰稿：凹语言 开发组</li><li>转载请注明原文链接：<a href="https://wa-lang.org/smalltalk/st0037.html" target="_blank" rel="noreferrer">https://wa-lang.org/smalltalk/st0037.html</a></li></ul><hr><p>凹语言设计的目标之一是简单易用。但是对于新接触编程的儿童教学来说依然有一定的门槛。为此开发组尝试将面向创意编程的 <a href="https://processing.org/" target="_blank" rel="noreferrer">Processing</a> 理念引入凹语言，通过 <code>js/p5</code> 包可以轻松实现一些简单的互动创意设计。</p><p>下面是来自杭州一小学三年级小学生的第一个凹语言程序：</p><p><img src="'+t+'" alt=""></p><p>通过VS Code将以上的程序输入电脑，编译并执行的效果如下：</p><p><img src="'+e+'" alt=""></p><p>程序本身也非常简单：首先通过 <code>import</code> 引入 P5 包；然后在 <code>init</code> 初始化一个长宽都是400的画布并设置一个灰色背景色；<code>Draw</code> 函数负责每一帧的绘制，根据鼠标是否按下绘制不同大小的圆形。</p><p>下面是杭州一小学二年级的小学生通过一个绘制线段的程序互动的效果：</p><p><img src="'+p+'" alt=""></p><p>目前 <code>js/p5</code> 包的功能还有限，我们会在小朋友学习的过程中逐步完善。希望未来每个中国的小学生都能通过我国的编程语言入门并进行日常开发。</p>',12),_=[c];function i(n,d,m,h,g,f){return s(),r("div",null,_)}const S=a(l,[["render",i]]);export{P as __pageData,S as default};
